mapper.insert:
                       
	 	INSERT INTO 
	 		product(prod_no, prod_name, prod_detail, manufacture_day, price, image_file, reg_date, brand_id, count) 
		VALUES	 
			(seq_product_prod_no.nextval, #{prodName}, #{prodDetail}, TO_CHAR(TO_DATE(#{manuDate}, 'YYYY-MM-DD'), 'YYYYMMDD'), #{price}, 
				#{fileName}, sysdate, #{brandIden.brandId}, #{count}
			)

mapper.select:
SELECT b.brand_name, prd.prod_name, prd.image_file, prd.prod_detail, prd.price, prd.manufacture_day, prd.count, prd.reg_date, prd.prod_no, prd.brand_id, p.part_name, p.price
FROM product prd, parts p, brand b WHERE b.brand_id = p.brand_id AND b.brand_id = prd.brand_id AND prd.prod_no = #{prodNo}
AND p.part_name = #{partName}

mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail}, price = #{price}, manufacture_day = TO_CHAR(TO_DATE(#{manuDate}, 'YYYY-MM-DD'), 'YYYYMMDD'),
brand_id = #{brandIden.brandId}, count = #{count}, image_file = #{fileName:VARCHAR}
WHERE prod_no = #{prodNo}
